import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Categories.css";

const sampleProducts = [
  { id: "whiskey-1", name: "Classic Whiskey", price: 45.0, image: "https://via.placeholder.com/300x200?text=Whiskey" },
  { id: "vodka-1", name: "Smooth Vodka", price: 30.0, image: "https://via.placeholder.com/300x200?text=Vodka" },
  { id: "wine-1", name: "Red Wine", price: 25.0, image: "https://via.placeholder.com/300x200?text=Wine" },
  { id: "beer-1", name: "Craft Beer (6-pack)", price: 18.0, image: "https://via.placeholder.com/300x200?text=Beer" },
  { id: "gin-1", name: "Premium Gin", price: 40.0, image: "https://via.placeholder.com/300x200?text=Gin" },
  { id: "tequila-1", name: "Aged Tequila", price: 55.0, image: "https://via.placeholder.com/300x200?text=Tequila" },
  { id: "champagne-1", name: "Sparkling Champagne", price: 70.0, image: "https://via.placeholder.com/300x200?text=Champagne" },
  { id: "rum-1", name: "Dark Rum", price: 35.0, image: "https://via.placeholder.com/300x200?text=Rum" },
  { id: "cider-1", name: "Apple Cider", price: 15.0, image: "https://via.placeholder.com/300x200?text=Cider" },
  { id: "brandy-1", name: "Fine Brandy", price: 50.0, image: "https://via.placeholder.com/300x200?text=Brandy" },
];

function addToCart(item) {
  const raw = localStorage.getItem("bw_cart");
  const cart = raw ? JSON.parse(raw) : [];
  cart.push(item);
  localStorage.setItem("bw_cart", JSON.stringify(cart));
}

export default function Categories() {
  const navigate = useNavigate();

  const handleClick = (product) => {
    addToCart(product);
    navigate("/cart");
  };

  return (
    <div className="categories-page">
      {/* Header */}
      <header className="header">
        <img src="logo-removebg-preview.png" alt="Logo" className="header-logo" />
        <nav className="header-nav">
          <Link to="/">Home</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/cart">Cart</Link>
        </nav>
      </header>

      <main className="categories-content">
        <h1>Our Categories</h1>

        <div className="categories-grid">
          {sampleProducts.map((p) => (
            <div key={p.id} className="category-card">
              <img src={p.image} alt={p.name} />
              <div className="card-body">
                <div className="card-header">
                  <strong>{p.name}</strong>
                  <span>${p.price.toFixed(2)}</span>
                </div>
                <button onClick={() => handleClick(p)}>Add to cart</button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
