import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductsPage = () => {
  const { category } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch(`http://127.0.0.1:5000/api/products/${category}`)
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error fetching products:", err));
  }, [category]);

  return (
    <div className="p-10 bg-gray-900 min-h-screen">
      <h2 className="text-center text-3xl font-bold text-white mb-8">
        {category.charAt(0).toUpperCase() + category.slice(1)} Selection
      </h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {products.map((p) => (
          <div
            key={p.id}
            className="rounded-xl shadow-lg overflow-hidden bg-gray-800"
          >
            <img src={p.image} alt={p.name} className="w-full h-40 object-cover" />
            <div className="p-4 text-white">
              <h3 className="text-lg font-bold">{p.name}</h3>
              <p className="text-sm">${p.price.toFixed(2)}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
