import React from "react";

function ProductCard({ product }) {
  return (
    <div className="bg-gray-900 text-white rounded-2xl p-4 shadow-md hover:scale-105 transition">
      <img
        src={product.image}
        alt={product.name}
        className="w-full h-48 object-cover rounded-lg mb-4"
      />
      <h3 className="font-bold text-lg">{product.name}</h3>
      <p className="text-gray-300">${product.price.toFixed(2)}</p>
      <button className="mt-3 bg-red-600 hover:bg-red-700 px-4 py-2 rounded-lg text-white font-semibold">
        Add to Cart
      </button>
    </div>
  );
}

export default ProductCard;
