import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css"; // import stylesheet

const isValidEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [agreed, setAgreed] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (!email.trim() || !password.trim()) {
      alert("Please fill in all fields.");
      return;
    }
    if (!isValidEmail(email)) {
      alert("Please enter a valid email address.");
      return;
    }
    if (!agreed) {
      alert("You must confirm you are 18 and above to proceed.");
      return;
    }
    navigate("/categories");
  };

  return (
    <div className="login-page">
      {/* HEADER */}
      <header className="login-header">
        <img
          src="/logo-removebg-preview.png"
          alt="Black and White Logo"
          className="login-logo"
        />
        <nav className="login-nav">
          <Link to="/login" className="nav-link">
            Login
          </Link>
          <Link to="/register" className="nav-link">
            Register
          </Link>
        </nav>
      </header>

      {/* MAIN */}
      <main className="login-main">
        <form onSubmit={handleLogin} className="login-card">
          <h2 className="login-title">Login</h2>
          <p className="login-subtitle">Sign in to your account</p>

          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              aria-label="Email"
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              aria-label="Password"
            />
          </div>

          <div className="checkbox-group">
            <input
              id="agreeLogin"
              type="checkbox"
              checked={agreed}
              onChange={() => setAgreed(!agreed)}
            />
            <label htmlFor="agreeLogin">I agree I am 18yrs and above</label>
          </div>

          <button type="submit" disabled={!agreed} className="login-btn">
            Login
          </button>

          <p className="login-footer">
            Don’t have an account?{" "}
            <Link to="/register" className="register-link">
              Register
            </Link>
          </p>
        </form>
      </main>
    </div>
  );
}
