// src/pages/CategoryPage.js

import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import ProductCard from "../components/ProductCard";

function CategoryPage() {
  const { categoryName } = useParams();
  const [drinks, setDrinks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const url = `/api/products/${categoryName}`;

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setDrinks(Array.isArray(data) ? data : []);
      })
      .catch((err) => {
        console.error("Failed to fetch drinks:", err);
        setDrinks([]);
      })
      .finally(() => setLoading(false));
  }, [categoryName]);

  return (
    <div className="bg-black min-h-screen text-white px-10 py-6">
      <Link to="/" className="text-gray-400 hover:text-white">
        &larr; Back to Categories
      </Link>

      <h2 className="text-2xl font-bold text-right capitalize">
        {categoryName.replace("_", " ")}
      </h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="grid md:grid-cols-3 gap-6 mt-6">
          {drinks.length > 0 ? (
            drinks.map((d) => (
              <ProductCard key={d.id} product={d} />
            ))
          ) : (
            <p>No drinks found for {categoryName}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default CategoryPage;
