import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../context/CartContext"; 
import Footer from "./Footer";
import "./Cart.css";

export default function Cart() {
  const { cart, removeFromCart, updateQuantity, getTotal } = useContext(CartContext);

  return (
    <div className="page-container">
      <div className="cart-page">
        {/* Header */}
        <header className="header">
          <img src="/logo-removebg-preview.png" alt="Logo" className="header-logo" />
          <nav className="header-nav">
            <Link to="/categories">Products</Link>
            <Link to="/cart">Cart ({cart.length})</Link>
          </nav>
        </header>

        {/* Cart */}
        <main className="cart-content">
          <h1>Cart</h1>

          {cart.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Item</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item) => (
                  <tr key={item.id}>
                    <td className="cart-item">
                      <img src={item.image} alt={item.name} />
                      <span>{item.name}</span>
                    </td>
                    <td>${item.price}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))}
                      />
                    </td>
                    <td>
                      <button
                        className="remove-btn"
                        onClick={() => removeFromCart(item.id)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
                <tr>
                  <td colSpan="3" className="cart-total-label">Total</td>
                  <td className="cart-total">${getTotal()}</td>
                </tr>
              </tbody>
            </table>
          )}

          {cart.length > 0 && (
            <button className="checkout-btn">Checkout</button>
          )}
        </main>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
}
