// src/pages/CategoryPage.js

import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import ProductCard from "../components/ProductCard";

function CategoryPage() {
  const { categoryName } = useParams();
  const [drinks, setDrinks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Example: for whiskey, we filter by ingredient “Whiskey”
    // or you might have another mapping
    const ingredient = categoryName; 
    const url = `https://www.thecocktaildb.com/api/json/v1/1/filter.php?i=${ingredient}`;

    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        if (data.drinks) {
          setDrinks(data.drinks);
        } else {
          setDrinks([]);
        }
      })
      .catch((err) => {
        console.error("Failed to fetch:", err);
        setDrinks([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [categoryName]);

  return (
    <div className="bg-black min-h-screen text-white px-10 py-6">
      <Link to="/" className="text-gray-400 hover:text-white">&larr; Back to Categories</Link>
      <h2 className="text-2xl font-bold text-right capitalize">{categoryName}</h2>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="grid md:grid-cols-3 gap-6 mt-6">
          {drinks.length > 0 ? (
            drinks.map((d) => (
              <ProductCard
                key={d.idDrink}
                product={{
                  id: d.idDrink,
                  name: d.strDrink,
                  image: d.strDrinkThumb,
                  // The API doesn't always provide a “price” — you might assign defaults
                  price: (Math.random() * 30 + 10).toFixed(2),
                }}
              />
            ))
          ) : (
            <p>No drinks found for {categoryName}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default CategoryPage;
