import React from "react";
import { Link } from "react-router-dom";
import "./Categories.css";

const categories = [
  { id: "whiskey", name: "Whiskey", description: "Explore our selection", image: "./w" },
  { id: "vodka", name: "Vodka", description: "Explore our selection", image: "./vodka.png" },
  { id: "wine", name: "Wine", description: "Explore our selection", image: "./wine.png" },
  { id: "beer", name: "Beer", description: "Explore our selection", image: "./beer.png" },
  { id: "rum", name: "Rum", description: "Explore our selection", image: "./rum.png" },
  { id: "tequila", name: "Tequila", description: "Explore our selection", image: "./tequila.png" },
];

export default function Categories() {
  return (
    <div className="categories-page">
      <header className="header">
        <img src="/logo-removebg-preview.png" alt="Logo" className="header-logo" />
        <nav className="header-nav">
          <Link to="/">Home</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/cart">Cart</Link>
        </nav>
      </header>

      <main className="categories-content">
        <h1>Our Categories</h1>

        <div className="categories-grid">
          {categories.map((cat) => (
            <div key={cat.id} className="category-card" style={{ backgroundImage: `url($www.thecocktaildb.com/api/json/v1/1/filter.php?c=Cocktail
})` }}>
              <div className="overlay">
                <h2>{cat.name}</h2>
                <Link to={`/products/${cat.id}`} className="explore-link">
                  {cat.description}
                </Link>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
