import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [agree, setAgree] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (!email || !password || !agree) {
      alert("Please fill in all fields and agree to the terms.");
      return;
    }
    navigate("/categories"); // after login, go to categories
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white">
      {/* Top-right links */}
      <div className="absolute top-6 right-10 flex space-x-6 text-black font-medium">
        <Link to="/login" className="hover:underline">
          Login
        </Link>
        <Link to="/register" className="hover:underline">
          Register
        </Link>
      </div>

      <form
        onSubmit={handleLogin}
        className="bg-white p-8 rounded-xl shadow-md w-96"
      >
        <h1 className="text-3xl font-bold text-center mb-2">Login</h1>
        <p className="text-gray-700 text-center mb-6">Sign in to your account</p>

        <div className="mb-4">
          <label className="block text-black mb-1">Email</label>
          <input
            type="email"
            className="w-full border border-gray-300 p-2 rounded"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div className="mb-4">
          <label className="block text-black mb-1">Password</label>
          <input
            type="password"
            className="w-full border border-gray-300 p-2 rounded"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <div className="flex items-center mb-4">
          <input
            type="checkbox"
            checked={agree}
            onChange={(e) => setAgree(e.target.checked)}
            className="mr-2"
          />
          <label className="text-black text-sm">I agree, I am 18 and above</label>
        </div>

        <button
          type="submit"
          className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded"
        >
          Login
        </button>

        <p className="text-center text-sm mt-4">
          Donâ€™t have an account?{" "}
          <Link to="/register" className="text-black underline">
            Register
          </Link>
        </p>
      </form>
    </div>
  );
}

export default Login;
