import React, { useState } from "react";
import { Link } from "react-router-dom";

function Login() {
  const [agreed, setAgreed] = useState(false);

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      minHeight: "100vh",
      backgroundColor: "white",
      fontFamily: "Arial, sans-serif",
      padding: "1rem"
    }}>
      <div style={{ width: "100%", maxWidth: "400px" }}>

        {/* Title */}
        <h2 style={{ fontSize: "2rem", fontWeight: "bold", color: "black", textAlign: "center", marginBottom: "0.5rem" }}>
          Login
        </h2>
        <p style={{ fontSize: "1rem", color: "black", textAlign: "center", marginBottom: "1.5rem" }}>
          Sign in to your account
        </p>

        {/* Email */}
        <div style={{ marginBottom: "1rem" }}>
          <label style={{ display: "block", marginBottom: "0.3rem", fontSize: "0.9rem" }}>Email</label>
          <input
            type="email"
            placeholder=""
            style={{ width: "100%", padding: "0.8rem", borderRadius: "6px", border: "1px solid #ccc", fontSize: "1rem" }}
          />
        </div>

        {/* Password */}
        <div style={{ marginBottom: "1rem" }}>
          <label style={{ display: "block", marginBottom: "0.3rem", fontSize: "0.9rem" }}>Password</label>
          <input
            type="password"
            placeholder=""
            style={{ width: "100%", padding: "0.8rem", borderRadius: "6px", border: "1px solid #ccc", fontSize: "1rem" }}
          />
        </div>

        {/* Checkbox */}
        <div style={{ display: "flex", alignItems: "center", marginBottom: "1.5rem", fontSize: "0.9rem" }}>
          <input
            type="checkbox"
            checked={agreed}
            onChange={() => setAgreed(!agreed)}
            style={{ marginRight: "0.5rem" }}
          />
          <span>I agree, I am 18 and above</span>
        </div>

        {/* Login Button */}
        <button
          disabled={!agreed}
          style={{
            width: "100%",
            padding: "0.8rem",
            backgroundColor: "#FF0000", // bright red
            color: "white",
            fontWeight: "bold",
            border: "none",
            borderRadius: "6px",
            cursor: agreed ? "pointer" : "not-allowed",
            marginBottom: "1rem"
          }}
        >
          Login
        </button>

        {/* Register link */}
        <p style={{ fontSize: "0.9rem", color: "black", textAlign: "center" }}>
          Donâ€™t have an account?{" "}
          <Link to="/register" style={{ color: "#FF0000", textDecoration: "none" }}>Register</Link>
        </p>
      </div>
    </div>
  );
}

export default Login;
