import React, { useState } from "react";
import { Link } from "react-router-dom";

function Login() {
  const [agreed, setAgreed] = useState(false);

  return (
    <div style={{ display: "flex", minHeight: "100vh", backgroundColor: "white", fontFamily: "Arial, sans-serif" }}>

      {/* Left side - Logo / placeholder */}
      <div style={{ flex: 1, display: "flex", alignItems: "center", justifyContent: "center" }}>
        <h1 style={{ fontSize: "2rem", fontWeight: "bold" }}>Black & White</h1>
      </div>

      {/* Right side - Login form */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center", padding: "2rem" }}>

        {/* Top navigation */}
        <div style={{ alignSelf: "flex-end", marginBottom: "2rem" }}>
          <Link to="/" style={{ marginRight: "1rem", color: "black", textDecoration: "none" }}>Login</Link>
          <Link to="/register" style={{ color: "black", textDecoration: "none" }}>Register</Link>
        </div>

        {/* Main form */}
        <div style={{ width: "100%", maxWidth: "400px", display: "flex", flexDirection: "column", alignItems: "center" }}>
          <h2 style={{ fontSize: "2rem", fontWeight: "bold", color: "black", marginBottom: "0.5rem" }}>Login</h2>
          <p style={{ fontSize: "1rem", color: "black", marginBottom: "1.5rem" }}>Sign in to your account</p>

          {/* Email */}
          <input
            type="email"
            placeholder="Email"
            style={{ width: "100%", padding: "0.8rem", marginBottom: "1rem", borderRadius: "6px", border: "1px solid #ccc", fontSize: "1rem" }}
          />

          {/* Password */}
          <input
            type="password"
            placeholder="Password"
            style={{ width: "100%", padding: "0.8rem", marginBottom: "1rem", borderRadius: "6px", border: "1px solid #ccc", fontSize: "1rem" }}
          />

          {/* Checkbox */}
          <label style={{ display: "flex", alignItems: "center", marginBottom: "1.5rem", fontSize: "0.9rem" }}>
            <input
              type="checkbox"
              checked={agreed}
              onChange={() => setAgreed(!agreed)}
              style={{ marginRight: "0.5rem" }}
            />
            I agree, I am 18 and above
          </label>

          {/* Login Button */}
          <button
            disabled={!agreed}
            style={{
              width: "100%",
              padding: "0.8rem",
              backgroundColor: agreed ? "#FF4500" : "#FFA07A",
              color: "white",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: agreed ? "pointer" : "not-allowed",
              marginBottom: "1rem"
            }}
          >
            Login
          </button>

          {/* Register link */}
          <p style={{ fontSize: "0.9rem", color: "black" }}>
            Donâ€™t have an account?{" "}
            <Link to="/register" style={{ color: "#FF4500", textDecoration: "none" }}>Register</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
