import React from "react";
import { Link } from "react-router-dom";

function Categories() {
  const categories = [
    { name: "Wines", image: "/images/wine.jpg" },
    { name: "Tequila", image: "/images/tequila.jpg" },
    { name: "Gin", image: "/images/gin.jpg" },
    { name: "Gas", image: "/images/gas.jpg" },
    { name: "Whiskey", image: "/images/whiskey.jpg" },
    { name: "Vodka", image: "/images/vodka.jpg" },
    { name: "Beer", image: "/images/beer.jpg" },
    { name: "Sparkling", image: "/images/sparkling.jpg" },
    { name: "Mixers", image: "/images/mixers.jpg" },
    { name: "Snacks", image: "/images/snacks.jpg" },
  ];

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white">
      <h1 className="text-3xl font-bold mb-8">Our Categories</h1>
      <div className="grid grid-cols-2 md:grid-cols-5 gap-6 max-w-5xl">
        {categories.map((cat) => (
          <div
            key={cat.name}
            className="bg-white rounded-lg shadow-md hover:shadow-lg cursor-pointer text-center"
          >
            <img
              src={cat.image}
              alt={cat.name}
              className="w-full h-40 object-cover rounded-t-lg"
            />
            <p className="p-2 font-medium">{cat.name}</p>
          </div>
        ))}
      </div>

      <div className="mt-10">
        <Link to="/checkout" className="text-white bg-black px-6 py-2 rounded-lg">
          Go to Checkout
        </Link>
      </div>
    </div>
  );
}

export default Categories;
